mapper.update:
UPDATE tab_installationaddresses 
SET is_del=#{deleteStatus.code},
WHERE install_sno=#{id}

mapper.select:
SELECT m.* FROM tab_installationaddresses m WHERE 1 = 1
AND user3_sno = #{param.agency.id}
AND install_fullAddress LIKE CONCAT('%', #{param.fullAddress}, '%')
AND install_contact like CONCAT('%', #{param.contact}, '%')
ORDER BY ${sortField} ${sortDir}
