mapper.select:
SELECT m.*
FROM tab_orders_Detailed m 
LEFT JOIN tab_orders o on m.orders_sno=o.orders_sno
WHERE 1 = 1 

	AND user3_sno = #{param.agency.id}


	AND o.orders_createtime > #{param.startDate}


	AND o.orders_createtime <= #{param.endDate}


	AND o.orders_payStatus= #{param.payStatus.code}


	AND o.orders_paytype= #{param.payType.code}


	AND o.chairs_sno=#{param.chair.id}


	AND m.user3_sno=#{param.agency.id}


	ORDER BY ${sortField} ${sortDir}
