mapper.select:
SELECT ISNULL(sum(orders_money),0)
FROM tab_orders 
WHERE 	orders_createtime >= #{startTime} 
	AND orders_createtime <  #{endTime} 
	
		AND user3_sno=#{id}

mapper.update:
UPDATE tab_orders SET orders_payStatus=#{payStatus.code}, orders_paytype=#{payType.code}, orders_minutes=#{minutes}, orders_money=#{money}, orders_check=#{checkCode},
user3_sno=0, user3_sno= #{agency.id},
chairs_sno=#{chair.id}, install_sno=#{installationAddress.id} WHERE orders_sno=#{id}

UPDATE tab_orders 
SET is_del=#{deleteStatus.code}
WHERE orders_sno=#{id}

UPDATE tab_orders 
SET orders_payStatus=#{payStatus.code}
WHERE orders_sno=#{id}

mapper.select:
SELECT m.*
FROM tab_orders m
WHERE 1 = 1 

	AND is_del = #{param.deleteStatus.code}


	AND user3_sno=#{param.agency.id}


	AND orders_createtime > #{param.startDate}


	AND orders_createtime <= #{param.endDate}


	AND install_sno= #{param.installationAddress.id}


	AND orders_payStatus= #{param.payStatus.code}


	AND orders_paytype= #{param.payType.code}


	AND chairs_sno=#{param.chair.id}


	ORDER BY ${sortField} ${sortDir}
